import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiService } from 'src/app/api.service';
import { UserService } from 'src/app/user/user.service';
import { Restaurant } from 'src/types/restaurant';

@Component({
  selector: 'app-current-restaurant',
  templateUrl: './current-restaurant.component.html',
  styleUrls: ['./current-restaurant.component.css'],
})
export class CurrentRestaurantComponent implements OnInit {
  restaurant = {} as Restaurant;
  isOwner: boolean = false;
  comments: Comment[] = [];
  constructor(
    private apiService: ApiService,
    private userService: UserService,
    private activeRoute: ActivatedRoute,
    private router: Router
  ) {}

  get isLoggedIn(): boolean {
    return this.userService.isLoggedIn;
  }

  ngOnInit(): void {
    this.activeRoute.params.subscribe((data) => {
      const id = data['restaurantId'];
      this.apiService.getRestaurant(id).subscribe((restaurant) => {
        this.restaurant = restaurant;
        localStorage.setItem('ownerId', restaurant._ownerId);
        this.isOwner = this.userService.user?._id === restaurant._ownerId;
      });
      this.apiService.getCommentService().subscribe((comments) => {
        this.comments = Array.from(comments);
        console.log(typeof this.comments);
      });
    });
  }

  removeRestaurant() {
    this.activeRoute.params.subscribe((data) => {
      const id = data['restaurantId'];
      this.apiService.deleteRestaurant(id).subscribe({
        next: () => {
          this.router.navigate(['/restaurants']);
        },
        error: () => {
          this.router.navigate(['/restaurants']);
        },
      });
    });
  }
}
<div class="container">
  <div class="card">
    <div class="card-image">
      <img src="{{ restaurant.img }}" />
    </div>
    <div class="card-text">
      <h2 class="card-title">{{ restaurant.name }}</h2>
      <p class="card-body">
        {{ restaurant.description }}
      </p>
      <span class="buttons" *ngIf="isOwner">
        <button
          class="button edit"
          routerLink="/restaurants/{{ restaurant._id }}/edit"
        >
          Edit
        </button>

        <button class="button delete" (click)="removeRestaurant()">
          Delete
        </button>
      </span>
    </div>

    <button
      class="card-add"
      *ngIf="isLoggedIn"
      routerLink="/restaurants/{{ restaurant._id }}/add-comment"
    >
      Add Comment
    </button>
  </div>
</div>
