import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from 'src/app/user/user.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css'],
})
export class HeaderComponent {
  constructor(private userService: UserService, private router: Router) {}

  get isLoggedIn(): boolean {
    return this.userService.isLogged;
  }

  get username(): string {
    return this.userService.user?.username || '';
  }

  logout() {
    this.userService.logout().subscribe({
      next: () => {
        this.router.navigate(['/home']);
      },
      error: () => {
        this.router.navigate(['/auth/login']);
      },
    });
  }
}
<header>
  <img
    src="https://upload.wikimedia.org/wikipedia/commons/thumb/f/fc/BG_Sofia_coa.svg/1200px-BG_Sofia_coa.svg.png"
  />
  <nav>
    <ul>
      <li><a routerLink="/home">Home</a></li>
      <li><a routerLink="/restaurants">Restaurants</a></li>
      <li><a routerLink="/add-restaurant">Add Restaurant</a></li>
      <li><a routerLink="/auth/login">Login</a></li>
      <li><a routerLink="/auth/register">Register</a></li>
      <li><a href="#">Logout</a></li>
    </ul>
  </nav>
</header>
