import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { environment } from 'src/environments/environment.development';
import { Comment } from 'src/types/comment';
import { Restaurant, RestaurantForUpdate } from 'src/types/restaurant';
import { User } from 'src/types/user';

@Injectable({
  providedIn: 'root',
})
export class ApiService {
  dataUrl = environment.dataUrl;
  constructor(private http: HttpClient) {}

  getRestaurants() {
    return this.http.get<Restaurant[]>(`${this.dataUrl}/restaurants`);
  }

  getRestaurant(id: string) {
    return this.http.get<Restaurant>(`${this.dataUrl}/restaurants/${id}`);
  }

  addRestaurant(
    name: string,
    type: string,
    location: string,
    img: string,
    description: string,
    comments: Comment[]
  ) {
    return this.http.post<Restaurant>(`${this.dataUrl}/restaurants`, {
      name,
      type,
      location,
      img,
      description,
      comments,
    });
  }

  editRestaurant(
    name: string,
    type: string,
    location: string,
    img: string,
    description: string,
    id: string
  ) {
    return this.http.put<RestaurantForUpdate>(
      `${this.dataUrl}/restaurants/${id}`,
      {
        name,
        type,
        location,
        img,
        description,
      }
    );
  }

  deleteRestaurant(id: string) {
    return this.http.delete<Restaurant>(`${this.dataUrl}/restaurants/${id}`);
  }

  addCommentService(
    text: string,
    userId: string,
    username: string,
    restaurantId: string
  ) {
    return this.http.put<Restaurant[]>(
      `${this.dataUrl}/restaurants/${restaurantId}`,
      {
        text,
        userId,
        username,
        restaurantId,
      }
    );
  }

  // getCommentService(id: string) {
  //   return this.http.get<Restaurant[]>(`${this.dataUrl}/${id}`);
  // }
}
