import { Component } from '@angular/core';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {

}
<form>
  <input
    type="text"
    name="name"
    class="question"
    id="name"
    required
    autocomplete="off"
  />
  <label for="nme"><span>What's your name?</span></label>
</form>
