import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { EMAIL_DOMAINS } from 'src/app/constants';
import { UserService } from '../user.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  domains = EMAIL_DOMAINS;
  constructor(private userService: UserService, private router: Router) {}

  login(form: NgForm) {
    if (form.invalid) {
      return;
    }

    // const { email, password } = form.value;

    // this.userService.login(email, password).subscribe(() => {
    //   this.router.navigate(['/themes']);
    // });
  }
}
<div class="container">
  <div class="screen">
    <div class="screen__content">
      <form class="login" #form="ngForm" (ngSubmit)="login(form)">
        <div class="login__field">
          <input
            type="email"
            name="email"
            class="login__input"
            placeholder="Email"
            ngModel
            #inputEmail="ngModel"
            required
          />
        </div>
        <ng-container *ngIf="inputEmail.touched">
          <p class="error" *ngIf="inputEmail.errors?.['required']">
            Email is required!
          </p>
          <p class="error" *ngIf="inputEmail.errors?.['emailValidator']">
            Email is not valid!
          </p>
        </ng-container>
        <div class="login__field">
          <input
            type="password"
            name="password"
            class="login__input"
            placeholder="Password"
            ngModel
            #inputPassword="ngModel"
            required
            minlength="5"
          />
        </div>
        <ng-container *ngIf="inputPassword.touched">
          <p class="error" *ngIf="inputPassword.errors?.['required']">
            Password is required!
          </p>
          <p class="error" *ngIf="inputPassword.errors?.['minlength']">
            Password must be at least 5 characters!
          </p>
        </ng-container>
        <button class="button login__submit">
          <span class="button__text">Log In Now</span>
        </button>
        <button class="button register__submit" routerLink="/auth/register">
          <span class="button__text">Register</span>
        </button>
      </form>
    </div>
    <div class="screen__background">
      <span class="screen__background__shape screen__background__shape4"></span>
      <span class="screen__background__shape screen__background__shape3"></span>
      <span class="screen__background__shape screen__background__shape2"></span>
      <span class="screen__background__shape screen__background__shape1"></span>
    </div>
  </div>
</div>
