import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ApiService } from 'src/app/api.service';
import { Restaurant } from 'src/types/restaurant';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css'],
})
export class SearchComponent {
  restaurants: Restaurant[] = [];
  constructor(private api: ApiService) {}

  Search(form: NgForm) {
    if (form.invalid) {
      return;
    }

    const { search } = form.value;
    console.log(search);

    this.api.getSearchedRestaurants(search).subscribe((restaurants) => {
      this.restaurants = restaurants;
    });
  }
}
<div>
  <form #form="ngForm" (ngSubmit)="Search(form)">
    <input
      type="text"
      name="search"
      id="form"
      ngModel
      #inputSearch="ngModel"
      required
      minlength="3"
      placeholder="Search for your favourite type..."
    />
    <ng-container *ngIf="inputSearch.touched">
      <p class="error" *ngIf="inputSearch.errors?.['required']">
        Search is required!
      </p>
      <p class="error" *ngIf="inputSearch.errors?.['minlength']">
        Search must be at least 3 characters!
      </p>
    </ng-container>
    <input type="submit" id="button" value="Search" />
  </form>
</div>

<div class="listing">
  <a [routerLink]="t._id" class="card" *ngFor="let t of restaurants">
    <span class="media">
      <img src="{{ t.img }}" />
    </span>
    <span class="name">{{ t.name }}</span>
    <span class="type">Type: {{ t.type }}</span>
    <span class="location">Location: {{ t.location }} </span>
    <span class="footer">
      <span class="button">Read Description</span>
    </span>
  </a>
</div>
