import { Component, OnInit } from '@angular/core';
import { ApiService } from 'src/app/api.service';
import { Restaurant } from 'src/types/restaurant';

@Component({
  selector: 'app-restaurants-list',
  templateUrl: './restaurants-list.component.html',
  styleUrls: ['./restaurants-list.component.css'],
})
export class RestaurantsListComponent implements OnInit {
  restaurants: Restaurant[] = [];
  constructor(private api: ApiService) {}

  ngOnInit(): void {
    this.api.getRestaurants().subscribe((restaurants) => {
      this.restaurants = restaurants;
    });
  }
}
<div class="listing" *ngFor="let t of restaurants">
  <a href="#" class="card">
    <span class="media">
      <img
        src="https://images.unsplash.com/photo-1682695795255-b236b1f1267d?q=80&w=300&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"
      />
    </span>
    <span class="title"> Card Title goes Here </span>
    <span class="description">
      The main description of the card should be here and somewhat long to show
      how we handle more text.
    </span>
    <span class="footer">
      <span class="button">Read more</span>
    </span>
  </a>
</div>
