import { Component } from '@angular/core';

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.css']
})
export class AboutComponent {

}
<body>
  <h1>We are Sofia Guide Restaurants</h1>
  <p>
    The idea of the site is to show people`s favorite places for eat. Every user
    can see the list of all uploaded restaurants and can search specific type in
    our search page. If you want to have access to all functionality of the
    application, there is Register button in the header. After successful
    register, the logged user can add restaurants in the site and also can write
    comments below every restaurant.
  </p>
</body>
