import { Component, OnInit } from '@angular/core';
import { ApiService } from 'src/app/api.service';
import { Restaurant } from 'src/types/restaurant';

@Component({
  selector: 'app-restaurants-list',
  templateUrl: './restaurants-list.component.html',
  styleUrls: ['./restaurants-list.component.css'],
})
export class RestaurantsListComponent implements OnInit {
  restaurants: Restaurant[] = [];
  loadedRestaurants: boolean = false;
  constructor(private api: ApiService) {}

  ngOnInit(): void {
    this.api.getRestaurants().subscribe((restaurants) => {
      console.log(restaurants);

      if (restaurants !== undefined) {
        this.loadedRestaurants = true;
      }
      this.restaurants = restaurants;
    });
  }
}
<body>
  <div class="listing">
    <a [routerLink]="t._id" class="card" *ngFor="let t of restaurants">
      <span class="media">
        <img src="{{ t.img }}" />
      </span>
      <span class="name">{{ t.name }}</span>
      <span class="type">Type: {{ t.type }}</span>
      <span class="location">Location: {{ t.location }} </span>
      <span class="footer">
        <span class="button">Read Description</span>
      </span>
    </a>
  </div>
</body>
