import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ApiService } from 'src/app/api.service';
import { Restaurant } from 'src/types/restaurant';

@Component({
  selector: 'app-current-restaurant',
  templateUrl: './current-restaurant.component.html',
  styleUrls: ['./current-restaurant.component.css'],
})
export class CurrentRestaurantComponent implements OnInit {
  restaurant = {} as Restaurant;
  constructor(
    private apiService: ApiService,
    private activeRoute: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.activeRoute.params.subscribe((data) => {
      const id = data['restaurantId'];
      this.apiService.getRestaurant(id).subscribe((restaurant) => {
        this.restaurant = this.restaurant;
      });
    });
  }
}
<div class="main">
  <ul class="cards">
    <li class="cards_item">
      <div class="card">
        <div class="card_image">
          <img
            src="https://assets.codepen.io/652/photo-1468777675496-5782faaea55b.jpeg"
            alt="mixed vegetable salad in a mason jar. "
          />
        </div>
        <div class="card_content">
          <h2 class="card_title">Farmstand Salad &#x2022; $9</h2>
          <div class="card_text">
            <p>
              Dig into the freshest veggies of the season! This salad-in-a-jar
              features a mixture of leafy greens and seasonal vegetables, fresh
              from the farmer's market.
            </p>
            <p>
              Served with your choice of dressing on the side: housemade ranch,
              cherry balsamic vinaigrette, creamy chipotle, avocado green
              goddess, or honey mustard. Add your choice of protein for $2 more.
            </p>
          </div>
        </div>
      </div>
    </li>
  </ul>
</div>
