import { Component, OnInit } from '@angular/core';
import { Validators, FormBuilder } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiService } from 'src/app/api.service';
import { Restaurant, RestaurantForUpdate } from 'src/types/restaurant';

@Component({
  selector: 'app-edit-restaurant',
  templateUrl: './edit-restaurant.component.html',
  styleUrls: ['./edit-restaurant.component.css'],
})
export class EditRestaurantComponent implements OnInit {
  restaurant = {} as Restaurant;

  restaurantForUpdate: RestaurantForUpdate = {
    name: '',
    type: '',
    location: '',
    img: '',
    description: '',
  };

  form = this.fb.group({
    name: ['', [Validators.required, Validators.minLength(4)]],
    type: ['', [Validators.required, Validators.minLength(4)]],
    location: ['', [Validators.required, Validators.minLength(6)]],
    img: [
      '',
      [Validators.required, Validators.pattern('https?://.*.(png|jpeg|jpg)')],
    ],
    description: ['', [Validators.required, Validators.minLength(10)]],
  });

  constructor(
    private fb: FormBuilder,
    private apiService: ApiService,
    private router: Router,
    private activeRoute: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.activeRoute.params.subscribe((data) => {
      const id = data['restaurantId'];
      this.apiService.getRestaurant(id).subscribe((restaurant) => {
        this.restaurant = restaurant;
      });
      const { name, type, location, img, description } = this.restaurant;
      this.restaurantForUpdate = { name, type, location, img, description };
      this.form.setValue({
        name,
        type,
        location,
        img,
        description,
      });
    });
  }
  // addRestaurant() {
  //   if (this.form.invalid) {
  //     return;
  //   }

  //   const { name, type, location, image, description } = this.form.value;

  //   this.api
  //     .addRestaurant(name!, type!, location!, image!, description!)
  //     .subscribe(() => {
  //       this.router.navigate(['/restaurants']);
  //     });
  // }

  editRestaurant() {}

  onCancel(e: Event): void {
    e.preventDefault();
    this.router.navigate(['/restaurants']);
  }
}
<div class="row">
  <h1>Edit your favourite<br />restaurant</h1>
</div>
<form [formGroup]="form" (ngSubmit)="editRestaurant()">
  <div class="row">
    <span>
      <input
        class="basic-slide"
        id="name"
        type="text"
        name="name"
        formControlName="name"
        [value]="restaurantForUpdate.name"
      />
      <label for="name">Name</label>
    </span>
    <ng-container *ngIf="form.get('name')?.touched">
      <p class="error" *ngIf="form.get('name')?.errors?.['required']">
        Name is required.
      </p>
      <p class="error" *ngIf="form.get('name')?.errors?.['minlength']">
        Name must be at least 4 characters long.
      </p>
