        name="type"
        formControlName="type"
        [value]="restaurantForUpdate.type"
      /><label for="type">Type</label>
    </span>
    <ng-container *ngIf="form.get('type')?.touched">
      <p class="error" *ngIf="form.get('type')?.errors?.['required']">
        Type is required.
      </p>
      <p class="error" *ngIf="form.get('type')?.errors?.['minlength']">
        Type must be at least 4 characters long.
      </p>
    </ng-container>
    <span>
      <input
        class="basic-slide"
        id="location"
        type="text"
        name="location"
        formControlName="location"
        [value]="restaurantForUpdate.location"
      /><label for="location">Location</label>
    </span>
